import React from 'react';
import Nav1 from '../components/nav1';
import Footer from '../components/footer';
import './style/search.css';

function ProductManage() {
  /*
  const navigate = useNavigate();

  const ProductClick = (event) => {
    event.preventDefault(); 
    navigate('/detail');
  };
  */

  
  let catagoriesArray = ["Tote bag","Shoulder Bag","Backpack","Handbag","Wallet"]

  let colorsArray = ["Blue","Green","Red","Black","White","Purple","Yellow","Brown","Gray","Pink"
                      ,"Orange","Magenta","Teal"]

  let priceRanges = [];
  for(let i = 1;i <= 10;i++){
    priceRanges.push((1000 * i) + "-" + (1000 * (1 + i)));
  }
  

  // let priceRangesArray = ["1000-2000","Shoulder Bag","Back pack","Handbag","Wallet"]
 
  return (
    <div>
      {/* <Nav2 /> */}
      <Nav1 />

      <section class = "bar">

          <div>
            <input type = "text" defaultValue={"search"}>
            </input>
            <img class="searchbutton"
            src={`${process.env.PUBLIC_URL}/assets/search.png`}
            alt="Search"/>
          </div>

          <select name="catagories" >
            <option value= "none" selected disabled hidden>catagory</option>
            {catagoriesArray.map((catagory) => { 
              return (<option value= {catagory}>{catagory}</option>);
          })} 
          {/* return value may change */}
          </select>

          <select name="color" >
            <option value= "none" selected disabled hidden>color</option>
            {colorsArray.map((color) => { 
              return (<option value= {color}>{color}</option>);
          })} 
          {/* return value may change */}
          </select>

          <select name="price range" >
            <option value= "none" selected disabled hidden>price</option>

            {priceRanges.map((priceRange) => (<option value = {priceRange}>{priceRange}</option>))}
          </select>
          
      </section>

      <Footer />
    </div>
  );
}

export default ProductManage;
