import React from 'react';
import styles from './com_style/recommend.css';
import { Link } from 'react-router-dom';

function Recommend(prop){

    

    return (<div class = {styles.component-container}>
                <section class = "recommended-container">
                {prop.bagsArray.map((bag) => {return(
                    <Link class = "recommended-bag-container" to = "/bag">
                        <img src = {bag.img} class = "img-container"></img>
                        <div class = "description-container">
                        <div class = "name">
                            {bag.name}
                        </div>
                        <div class = "catagory">
                            {bag.catagory}
                        </div>
                        <div class = "price">
                            {"THB " + bag.price + ".00"}
                        </div>
                        </div>
                    </Link>)})}
                </section>
            </div>);

}

export default Recommend;
