import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Nav1 from '../components/nav1';
import Footer from '../components/footer';
import './style/login.css'

const handleLogin = (username,password) => {
  console.log(username);
  console.log(password);
}
// handleLogin(document.getElementById("username"),document.getElementById("password"))

function dumb(){
  console.log("dumb");
}

function Login() {
  // const [username, setUsername] = useState(null);
  // const [password, setPassword] = useState(null);
  // const navigate = useNavigate();

  // const handleLogin = (event) => {
  //   event.preventDefault();

  //   navigate('/');
  // };
  

  return (
    <div>
      <Nav1 />

        <div class = "header">
        <img  class = "userLogo" src={`${process.env.PUBLIC_URL}/assets/user1.png`} alt="user" />
        <label>Administrator Log In</label>
        </div>

      <div class = "page-container">
        <div class = "login-container">
            
            <div class = "header">
            Administrator Log In
            </div>

            <form class = "form" >
              <div>Username:</div>

              <div class = "center" >
                <input type="text" placeholder="username" id = "username" required/>
              </div>

              <div>Password:</div>

              <div class = "center" >
              <input type="text" placeholder="password" id = "password" required/>
              </div>

              <div class = "center">
                <button class = "submitButton" onClick={handleLogin(document.getElementById("username"),document.getElementById("password"))}>
                  log in
                </button>
              </div>

            </form>

            


        </div>
      </div>



        {/* <form  onSubmit={handleLogin}>
          <div>
            <label htmlFor="username">Username:</label>
            <input
              id="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Login</button>
        </form> */}



      <Footer />
    </div>
  );
}

export default Login;
