import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'
import Nav2 from '../components/nav2';
import Header from '../components/header';
import Footer from '../components/footer';
import './style/editproduct.css'

// https://media.tanachira.co.th/rec/production/marimekko/media/products/ea103a3245974f23b6d4793707e057f3.jpg
const EditProduct = (props) => {

    const [mode,setMode] = useState("ADD");
    const location = useLocation();
    let EditBag;
    EditBag = location.state;

    const [name,setName] = useState("");
    const [catagory,setCatagory] = useState(null);
    const [color,setColor] = useState(null);
    const [stock,setStock] = useState(null);
    const [price,setPrice] = useState(null);
    const [description,setDescription] = useState("");
    const [pictureArray,setPictureArray] = useState(["1","2","",""]);



    //test data
    let catagoriesArray = ["Tote bag","Shoulder Bag","Backpack","Handbag","Wallet"]

    //test data
    let colorsArray = ["Blue","Green","Red","Black","White","Purple","Yellow","Brown","Gray","Pink"
                        ,"Orange","Magenta","Teal"]

    const BI = ["/bagSample1.jpg","/IMG_2023.jpg","/bagSample2.jpg","/IMG_2023.jpg"]
    const desp = "The Mono Mini Tote is made of organic cotton. The large Marimekko “M” is printed in the front. The small bag has handles and an open pocket on the outside\nSIZE\nHeight: 23.00 cm\nWidth: 31.00 cm\nDepth: 14.50 cm\nMain Material: 100 % Cotton\n";
    const staticFilePath = "http://localhost:2999/picture";

    const HandleAddBag = (e) => {
        e.preventDefault();
        alert(pictureArray);
        // id is 0 but in server, new id need to be allocated
        const Bag = {BagId : 0,name : name,catagory : catagory, color : color ,BagStock : stock , price : price,
        BagDescription : description , BagImages : pictureArray}
        fetch('http://localhost:2999/post_bag_api',{ method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(Bag)})
        .catch(error => console.error(error));

    }

    const ReturnBagImageLink = (index) => {
        const copy = pictureArray;
        return copy[index - 1];
    }

    const pictureInputContainer = (name,index) => {
        return(
            <section class = "editor-container"> {/* product name*/}

            <div class = "side-container">
                {name}
            </div>
            <div class = "center-container">
                <input type = "text" class = "editor-box" id = "name" onChange={(e) => {pictureArray[index - 1] = e.target.value;setPictureArray(pictureArray)}}>
                </input>


                <img src = "https://media.tanachira.co.th/rec/production/marimekko/media/products/ea103a3245974f23b6d4793707e057f3.jpg" alt = "bagimage">
                </img>
     
                {pictureArray.map((p) => (<div>{p}</div>))}
    
                {/* staticFilePath + "/image-placeholder.jpg" */}
            </div>
            <div class = "side-container"></div> 
            </section>
        );
    }


    return (<div class = "page-container-editproduct">

        <Header></Header>
        <Nav2></Nav2>
        <section class = "page-title">

            <img src = {staticFilePath + "/product.png"} alt="productedit" class = "logo"></img>
            {mode == "ADD"?"Product Management - Add Product" : "Product Management - Edit Product"} 

        </section>

        <div class = "editors-container" >

            {pictureInputContainer("picture link 1",1)}


            {pictureInputContainer("picture link 2",2)}


            {pictureInputContainer("picture link 3",3)}


            {pictureInputContainer("picture link 4",4)}

            <section class = "editor-container"> {/* product name*/}

            <div class = "side-container">
                Product Name:
            </div>
            <div class = "center-container">
                <input type = "text" class = "editor-box" id = "name" onChange={(e) => setName(e.target.value)}>
                </input>
            </div>
            <div class = "side-container"></div> 
            </section>
            

            {/* "Tote bag","Shoulder Bag","Backpack","Handbag","Wallet" */}
            <section class = "editor-container"> {/* product catagory*/}
                
            <div class = "side-container">
                Categories:
            </div>
            <div class = "center-container">
                <select class = "editor-box" id = "catagory" onChange={(e) => setCatagory(e.target.value)}>
                    <option value = "none">not selected</option>

                    {catagoriesArray.map((catagory) => {return (<option value = {catagory}>{catagory}</option>)})}
                </select>
            </div>
            <div class = "side-container"></div>
            </section>

            <section class = "editor-container"> {/* product color*/}
                
            <div class = "side-container">
                Color:
            </div>

            <div class = "center-container">
                <select class = "editor-box" id = "color" onChange={(e) => setColor(e.target.value)}>
                    <option value = "none" selected>not selected</option>

                    {colorsArray.map((color) => {return (<option value = {color}>{color}</option>)})}

                </select>
            </div>
            <div class = "side-container"></div>
            </section>


            <section class = "editor-container"> {/* product stock*/}
                
            <div class = "side-container">
                Stock:
            </div>
            <div class = "center-container">
                <input type = "text" class = "editor-box" id = "stock" onChange={(e) => setStock(e.target.value)}>
                </input>
            </div>
            <div class = "side-container"></div>
            </section>


            <section class = "editor-container"> {/* product price*/}
                
            <div class = "side-container">
                Price:
            </div>
            <div class = "center-container">
                <input type = "text" class = "editor-box" id  = "price" onChange={(e) => setPrice(e.target.value)}>
                </input>
            </div>
            <div class = "side-container"></div>
            </section>


            <section class = "editor-container"> {/* product description*/}
                
            <div class = "side-container">
                Description:
            </div>
            <div class = "center-container">
                <textarea onChange={(e) => setDescription(e.target.value)}>
                    
                </textarea>
            </div>
            <div class = "side-container" rows="4" cols="50" id  = "description"></div>
            </section>

            <button class = "confirm-button"  onClick={(e) => {HandleAddBag(e)}} >
                {mode == "ADD"?"ADD":"SAVE"}
            </button>
            
        </div>




        <Footer></Footer>











    </div>);


}

export default EditProduct;
