import React, { useState } from 'react';
import Header from '../components/header';
import Nav2 from '../components/nav2';
import Footer from '../components/footer';
import { Link } from 'react-router-dom';
import './style/productmanage.css'

function ProductManage() {
    
    // const navigate = useNavigate();
  
    // const EditProduct = (event) => {
    //     event.preventDefault(); 
    //     navigate('/addproduct');
    //   };
    // const handleSearchIconClick = (event) => { // edit search button hear
    //     event.preventDefault(); 
    //     navigate('/productmanage');
    // };

    const [totalProduct,setTotalProduct] = useState(300);
  
    return (
      <div>
        <Header />
        <Nav2 />

        <section class = "page-container-productmanagement">

            <section class = "nav">

              <div class = "left">
              <img class = "logo" src={process.env.PUBLIC_URL + "/assets/product.png"} alt="productmanage" />
              Product management
              </div>

              <div class = "right">
                <button class = "addButton">
                  add new
                </button>
              </div>
              
            </section>

            <section class = "mainNav">

              <div class = "container flex-start">
                  <div class = "searchBar">
                    <input class = "searchInput" placeholder='search' type="text"></input>
                    <img class = "searchButton" src = {process.env.PUBLIC_URL + "/assets/search.png"}></img>
                  </div>
              </div>
              <div class = "container center">
                  <div class = "catagoryButton">
                    <img class = "searchButton" src = {process.env.PUBLIC_URL + "/assets/down-arrow.png"}></img>
                    catagory
                  </div>
              </div>
              <div class = "container"></div>
            
            </section>

            {/* {totalProduct + " Products"} */}

            <section class = "edit-list-container">
              <table>

                <thead>
                <tr>
                  <th>No.</th>
                  <th>IMG</th>
                  <th>Name:</th>
                  <th>catagories:</th>
                  <th>Color:</th>
                  <th>Stock:</th>
                  <th>Price:</th>
                </tr>
                </thead>
                
              </table>
            </section>

        </section>
        

        {/* <div className="ProductManagement">
            <img src={`${process.env.PUBLIC_URL}/assets/product.png`} alt="productmanage" />
            <h1>Products Management</h1>
            <Link to="/addproduct">Add new</Link>
        </div>
        <div>
        <input type="text" placeholder="Search..." />
        <button>
          <img 
              src={`${process.env.PUBLIC_URL}/assets/search.png`} 
              alt="Search" 
              onClick={handleSearchIconClick} // Attach the click handler here
          />
        </button>
        </div> */}

        <Footer />
      </div>
    );
  }
  
  export default ProductManage;
