import React from 'react';
import Nav2 from '../components/nav2';
import Footer from '../components/footer';
import { Link, useNavigate } from 'react-router-dom';

function ProductManage() {
  /*
  const navigate = useNavigate();

  const ProductClick = (event) => {
    event.preventDefault(); 
    navigate('/detail');
  };
  */

  let recommendedArray = [{name : "Bag1", catagory : "Tota Bag", price : 2000, img : process.env.PUBLIC_URL + "/assets/bagSample1.jpg"},
                          {name : "Bag2", catagory : "Tota Bag", price : 2100, img : process.env.PUBLIC_URL + "/assets/bagSample2.jpg"},
                          {name : "Bag3", catagory : "Shoulder Bag", price : 2200, img : process.env.PUBLIC_URL + "/assets/bagSample1.jpg"},
                          {name : "Bag4", catagory : "Shoulder Bag", price : 6300, img : process.env.PUBLIC_URL + "/assets/bagSample2.jpg"}]

  
  let catagoriesArray = ["Tote bag","Shoulder Bag","Back pack","Handbag","Wallet"]

  let colorsArray = ["Blue","Green","Red","Black","White","Purple","Yellow","Brown","Gray","Pink"
                      ,"Orange","Magenta","Teal"]

  

  // let priceRangesArray = ["1000-2000","Shoulder Bag","Back pack","Handbag","Wallet"]
 
  return (
    <div>
      <Nav2 />

      <section>
          <input type = "text" defaultValue={"search"}></input>
          <select name="catagories" >
            <option value= "none" selected disabled hidden>catagory</option>
            {catagoriesArray.map((catagory) => { 
              return (<option value= {catagory}>{catagory}</option>);
          })} 
          {/* return value may change */}
          </select>

          <select name="color" >
            <option value= "none" selected disabled hidden>color</option>
            {colorsArray.map((color) => { 
              return (<option value= {color}>{color}</option>);
          })} 
          {/* return value may change */}
          </select>

          <select name="price range" >
            <option value= "none" selected disabled hidden>price</option>

            {() => {
              let returnedComponmented = [];
              let tmp;
              for(let i = 1;i <= 10;i++){
                tmp = (1000 * i) + "-" + (1000 * (1 + i));
                returnedComponmented.push(<option value= {tmp}>{color}</option>);
              }
              return returnedComponmented;
            }}

            {colorsArray.map((color) => { 
              return (<option value= {color}>{color}</option>);
          })} 
          {/* return value may change */}
          </select>
      </section>

      <Footer />
    </div>
  );
}

export default ProductManage;
