import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'
import Nav2 from '../components/nav2';
import Header from '../components/header';
import Footer from '../components/footer';
import './style/editproduct.css'


const EditProduct = (props) => {

    const [mode,setMode] = useState("ADD");
    const location = useLocation();
    let Bag;
    Bag = location.state;

    const [name,setName] = useState("");
    const [catagory,setCatagory] = useState(null);
    const [color,setColor] = useState(null);
    const [stock,setStock] = useState(null);
    const [price,setPrice] = useState(null);
    const [description,setDescription] = useState("");
    const [pictureArray,setPictureArray] = useState(["","","",""]);



    const BI = ["/bagSample1.jpg","/IMG_2023.jpg","/bagSample2.jpg","/IMG_2023.jpg"]
    const desp = "The Mono Mini Tote is made of organic cotton. The large Marimekko “M” is printed in the front. The small bag has handles and an open pocket on the outside\nSIZE\nHeight: 23.00 cm\nWidth: 31.00 cm\nDepth: 14.50 cm\nMain Material: 100 % Cotton\n";
    const staticFilePath = "http://localhost:2999/picture";

    const HandleAddBag = (e) => {
        e.preventDefault();

        const name = document.getElementById("name");
        const catagory = document.getElementById("catagory");
        const color = document.getElementById("color");
        const stock = document.getElementById("stock");
        const price = document.getElementById("price");
        const description = document.getElementById("description");

        const insertedBag = {bag : Bag};
        fetch('http://localhost:2999/post_bag_api',{ method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(insertedBag)});

        // fetch('http://localhost:2999/login_api', 
        // { method: 'POST', headers: { 'Content-Type': 'application/json' }, 
        // body: JSON.stringify(unverifiedUser)})
        // .then(setIsLogIn(!isLogIn))
        // .catch(error => console.error(error));

    }

    const pictureInputContainer = (name) => {
        return(
            <section class = "editor-container"> {/* product name*/}

            <div class = "side-container">
                {name}
            </div>
            <div class = "center-container">
                <input type = "text" class = "editor-box" id = "name" >
                </input>
            </div>
            <div class = "side-container"></div> 
            </section>
        );
    }

    return (<div class = "page-container-editproduct">

        <Header></Header>
        <Nav2></Nav2>
        <section class = "page-title">

            <img src = {staticFilePath + "/product.png"} alt="productedit" class = "logo"></img>
            {mode == "ADD"?"Product Management - Add Product" : "Product Management - Edit Product"} 

        </section>

        <div class = "editors-container" >




            <section class = "editor-container"> {/* product name*/}

            <div class = "side-container">
                Product Name:
            </div>
            <div class = "center-container">
                <input type = "text" class = "editor-box" id = "name" onChange={(e) => setName(e.target.value)}>
                </input>
            </div>
            <div class = "side-container"></div> 
            </section>
            


            <section class = "editor-container"> {/* product catagory*/}
                
            <div class = "side-container">
                Categories:
            </div>
            <div class = "center-container">
                <input type = "text" class = "editor-box" id = "catagory" onChange={(e) => setCatagory(e.target.value)}>
                </input>
            </div>
            <div class = "side-container"></div>
            </section>

            <section class = "editor-container"> {/* product color*/}
                
            <div class = "side-container">
                Color:
            </div>
            <div class = "center-container">
                <input type = "text" class = "editor-box" id = "color" onChange={(e) => setColor(e.target.value)}>
                </input>
            </div>
            <div class = "side-container"></div>
            </section>


            <section class = "editor-container"> {/* product stock*/}
                
            <div class = "side-container">
                Stock:
            </div>
            <div class = "center-container">
                <input type = "text" class = "editor-box" id = "stock" onChange={(e) => setColor(e.target.value)}>
                </input>
            </div>
            <div class = "side-container"></div>
            </section>


            <section class = "editor-container"> {/* product price*/}
                
            <div class = "side-container">
                Price:
            </div>
            <div class = "center-container">
                <input type = "text" class = "editor-box" id  = "price">
                </input>
            </div>
            <div class = "side-container"></div>
            </section>


            <section class = "editor-container"> {/* product description*/}
                
            <div class = "side-container">
                Description:
            </div>
            <div class = "center-container">
                <textarea>
                    
                </textarea>
            </div>
            <div class = "side-container" rows="4" cols="50" id  = "description"></div>
            </section>

            <button class = "confirm-button"  onClick={(e) => {HandleAddBag(e)}} >
                {mode == "ADD"?"ADD":"SAVE"}
            </button>
            
        </div>




        <Footer></Footer>











    </div>);


}

export default EditProduct;
