import React, { useState , useEffect } from 'react';
import Header from '../components/header';
import Nav2 from '../components/nav2';
import Footer from '../components/footer';
import { Link } from 'react-router-dom';
import './style/accountmanage.css'
import PageNavBlock from '../components/pagenavblock';

function AccountManage() {
    /*
    const navigate = useNavigate();
  
    const ProductClick = (event) => {
      event.preventDefault(); 
      navigate('/detail');
    };
    */

    // let adminsArray = [{AdminID : 1,AdminRole : "edit product",AdminUsername : "U1",AdminFirstName : "F1",AdminSurname : "S1", AdminEmail : "E1", AdminPassword : "PW1"}
    //                 ,{AdminID : 2,AdminRole : "edit product",AdminUsername : "U2",AdminFirstName : "F2",AdminSurname : "S2", AdminEmail : "E2", AdminPassword : "PW2"}
    //                 ,{AdminID : 3,AdminRole : "edit account",AdminUsername : "U3",AdminFirstName : "F3",AdminSurname : "S3", AdminEmail : "E3", AdminPassword : "PW3"}
    //                 ,{AdminID : 4,AdminRole : "remove product",AdminUsername : "U4",AdminFirstName : "F4",AdminSurname : "S4", AdminEmail : "E4", AdminPassword : "PW4"}
    //                 ,{AdminID : 5,AdminRole : "add product",AdminUsername : "U5",AdminFirstName : "F5",AdminSurname : "S5", AdminEmail : "E5", AdminPassword : "PW5"}];

    // const adminsArray = [{userName : "mart1az",email : "marz@gmail",firstName : "marz"}
    //                     ,{userName : "james303",email : "james@gmail",firstName : "james"}
    //                   ,{userName : "heisenberg",email : "walterwhite@gmail",firstName : "walter"}
    //                   ,{userName : "mike",email : "kime@gmail",firstName : "mike"}
    //                   ,{userName : "mart1az",email : "marz@gmail",firstName : "marz"}
    //                   ,{userName : "mart1az",email : "marz@gmail",firstName : "marz"}
    //                   ,{userName : "mart1az",email : "marz@gmail",firstName : "marz"}
    //                   ,{userName : "mart1az",email : "marz@gmail",firstName : "marz"}
    //                   ,{userName : "mart1az",email : "marz@gmail",firstName : "marz"}];
    
    
    const [totalAdmin,setTotalAdmin] = useState(adminsArray.length);
    const itemPerPage = 5;
    const [totalPage,setTotalPage] = useState(Math.floor(totalAdmin/itemPerPage) + 1);
    const [currentPage,setCurrentPage] = useState(1);
    const [admins,setAdmins] = useState([]);

    const [hasLoad,setHasLoad] = useState(true);
    const [reload,setReload] = useState(true);
    const [searchId,setSearchId] = useState('');


    useEffect(() => {
      
      fetch('http://localhost:2999/search_api') // 'http://localhost:3001/api/bag'
      .then(response => response.json())
      .then(data => {setAdmins(data);totalPage(Math.floor(data.length/itemPerPage) + 1);setCurrentPage(1);})
      .then(setReload(!reload))
      .catch(error => console.error('Error fetching admin:', error));

    }, [hasLoad]);


    useEffect(() => { // reload the page without triggering reset???
      setReload(!reload);
    },[reload]);


    const handleSearchAdminById = () => {
      let url;
      url = `http://localhost:2999searchAdminId_api/?id=${searchId}`;

      fetch(url)
      .then(res => res.json())
      .then(data => {setAdmins(data); 
        setTotalPage(Math.floor(data.length/itemPerPage) + 1);
        setCurrentPage(1)})
      .then(setReload(!reload));
  
    }

    function renderAdminRow(){
      const returnedList = [];
      for(let i = (currentPage - 1) * itemPerPage;i < currentPage * itemPerPage && i < admins.length;i++){
        returnedList.push(<tr>
          <td>{admins[i].AdminUsername}</td>
          <td>{admins[i].AdminUsername}</td>
          <td>{admins[i].AdminEmail}</td>
          
          <td>
            <Link to = "/editadmin" state = { {EditAdmin : admins[i], Mode : "ADD"} } >
              <img class = "editButton" src = {process.env.PUBLIC_URL + "/assets/edit-text.png"}></img>
            </Link>
          </td>
          <td>
          <Link to = "/editadmin" state = { {EditAdmin  : admins[i], Mode : "EDIT"} } >
            <img class = "editButton" src = {process.env.PUBLIC_URL + "/assets/edit-text.png"}></img>
            </Link>
          </td>
        </tr>);
      }
     
      return returnedList;
    }
                  
    return (
      <div>
        <Header/>
        <Nav2 />
        <section class = "page-container-accountmanagement">

          <section class = "nav">

            <div class = "left">
            <img class = "logo" src={process.env.PUBLIC_URL + "/assets/system.png"} alt="productmanage" />
            account management
            </div>

            <div class = "right">
              <button class = "addButton">
                add new
              </button>
            </div>
              
          </section>

          <section class = "mainNav">

            <div class = "searchBar">
              <input class = "searchInput" placeholder='search' type="text" value = {searchId} onChange={(e) => setSearchId(e.target.value)}></input>
              <img class = "searchButton" src = {process.env.PUBLIC_URL + "/assets/search.png"} onClick={() => handleSearchAdminById()}></img>
            </div>
            
          </section>

          <section class = "edit-table-container">

            <table class = "edit-table">

              <thead>
              <tr>
                
                <th>username:</th>
                <th>firstname:</th>
                <th>email</th>
                <th>A</th>
                <th>D</th>
  
              </tr>
              </thead>

              <tbody>
              {/* <tr>
                
                <td>username:</td>
                <td>firstname:</td>
                <td>email</td>
                <td>A</td>
                <td>D</td>
  
              </tr> */}

                  {renderAdminRow()}

              </tbody>
              
              
            </table>

          </section>
          {<PageNavBlock currentPage = {currentPage} numberOfPage = {totalPage} setCurrentPage = {setCurrentPage}/>}
        </section>
        <Footer />
      </div>
    );
  }
  
  export default AccountManage;
